	private Speaker[] speakers = Array.Empty<Speaker>();
	private Event[] currentEvent = Array.Empty<Event>();

	protected override async Task OnInitializedAsync()
	{
		await GetCurrentEvent();
		await GetSpeaker();
		await InvokeAsync(() =>
		{
			StateHasChanged();
